package testst;

public class SwithDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String city = "amalapuram";
		switch (city) {

		case "Hyderabad":
			System.out.println("Hyderabad");
			break;

		case "chennai":
			System.out.println("chennai");
			break;

		case "bangolore":
			System.out.println("bangolore");
			break;

		case "gannavaram":
			System.out.println("gannavaram");
			break;

		default:
			System.out.println("enter valid city");
			break;

		}
	}

}
